import React, {Component} from 'react';
import axios from 'axios'

const url = 'http://app.linkedin-reach.io/words'
const path = '/api/words'
const param = '?difficulty=10'

class Game extends Component {       
    constructor(props){
        super(props)
        this.state = {
            currentWord: "",
            loading: true,
            charactersPlayed: "",
            charSlots: [],
            correctlyGuessed: [],
            incorrectlyGuessed: [],
            numberOfGuesses: 6,
            gameOver: false
        }
        this.characters = "abcdefghijklmnopqrstuvwxyz12367890";
        this.baseState = this.state;
    }

    fetchWord = () => {
        axios.get('/api/words' + param)
        .then(response => {
          return response.data.split("\n")
        })
        .then(allWords => {
          const word = allWords[Math.floor(allWords.length * Math.random())]
          return word
        })
        .then((word) => {
            console.log(word)
            this.setState({
                currentWord: word,
                loading: false
            })
        })
    }

    componentDidMount(){
        this.fetchWord();
    }

    handleClick = (char, e) => {
        if (e) e.preventDefault();

    }

    handleReset = () => {
        this.setState({...this.baseState
        });
    }

    loading = () => (<div>Loading..</div>)


    render(){
        if (this.state.loading){
            return this.loading()
        } else if (this.state.gameOver){
            return (
                <div>
                    <div>The game is over</div>
                    <div> check winner</div>
                    <div> add button to restart game</div>
                </div>
            )
        } else { 
            return (
                <div>
                    <h1>Hangman Game</h1>
                </div>
            )
        }
    }
}

export default Game
