const initialState = {
    turn: 0,
    gameNumber: 0,
    correctGuesses: [],
    incorrectGuesses: [],
    word: "",
}


function game(state = initialState, action){
    switch (action.type){
        case START_GAME:
            return {...state, gameNumber: state.gameNumber + 1}
        default:
            return state
    }
}

export default game;
