import React, {Component} from 'react';
import axios from 'axios'

const url = 'http://app.linkedin-reach.io/words'
const path = '/api/words'
const param = '?difficulty=10'

class Game extends Component {       
    constructor(props){
        super(props)
        this.state = {
            currentWord: "",
            loading: true,
            charactersPlayed: "",
            charSlots: [],
            gussed: [],
            correctlyGuessed: [],
            incorrectlyGuessed: [],
            numberOfGuesses: 6,
            gameOver: false
        }
        this.characters = "abcdefghijklmnopqrstuvwxyz12367890";
        this.baseState = this.state;
    }

    fetchWord = () => {
        axios.get('/api/words' + param)
        .then(response => {
          return response.data.split("\n")
        })
        .then(allWords => {
          const word = allWords[Math.floor(allWords.length * Math.random())]
          return word
        })
        .then((word) => {
            const wordHash = word.split("").map((char)=> ({character: char, guessed: false}))
            console.log(wordHash);
            this.setState({
                currentWord: wordHash,
                loading: false
            })
        })
    }

    componentDidMount(){
        this.fetchWord();
    }

    checkGameWon = (guessedChars) => {
        return guessedChars.reduce((winState, currentObject) => {
            return winState && currentObject.guessed;
          }, true);
    }

    handleClick = (char, e) => {
        let bool = this.state.currentWord.includes(char);
        if(bool){
            let currentWord = this.state.currentWord.map((curWord)=>{
                if (curWord.character === char) {
                    return {...curWord, guessed: true}
                }
                return curWord;
            })

            let gameWon = this.checkGameWon(currentWord);

            this.setState((prevState) => {
                return {
                    currentWord, 
                    guessed: prevState.guessed.concat(char),
                    gameWon: gameWon
                }
            })
        } else {
            this.setState((prevState, _) => {
            const numberOfGuesses = prevState.numberOfGuesses - 1;
            let stateUpdate = {
                numberOfGuesses: numberOfGuesses,
            };
            // Kill the game if needed
            if (numberOfGuesses === 0) {
                stateUpdate.gameOver = true;
            }
            // Update the letters already tried
            stateUpdate.guessed = [char].concat(prevState.guessed);
            return stateUpdate;
        })
        }
    }

    handleReset = () => {
        this.setState({...this.baseState});
    }

    loading = () => (<div>Loading..</div>)


    render(){
        if (this.state.loading){
            return this.loading()
        } else if (this.state.gameOver){
            return (
                <div>
                    <div>The game is over</div>
                    <div> check winner</div>
                    <div> add button to restart game</div>
                </div>
            )
        } else { 
            return (
                <div>
                    <h1>Hangman Game</h1>
                </div>
            )
        }
    }
}

export default Game
