import React, {Component} from 'react'

class SecretWord extends Component {
    constructor(props){
        super(props)
        this.state = {}
    }

    componentDidMount(){
        this.createWordSlot();
    }

    createWordSlot = () => {
        var wordSlots = this.props.currentWord.split("").map((el) => "_")
        this.setState({wordSlot: wordSlots.join(" ")})
    }

    render(){
        return (
            <div>
                <ul>
                    <li>
                        <p>{this.props.currentWord}</p>
                    </li>
                    <li>
                        <p>{this.state.wordSlot}</p>
                    </li>
                    <li>
                        <p>{this.props.guessedCharacters}</p>
                    </li>
                </ul>
            </div>
        );
    }
}

export default SecretWord
