const START_GAME = "START_GAME";

const initialState = {
    turn: 0,
    playerName: "",
    gameNumber: 0,
    correctGuesses: [],
    incorrectGuesses: [],
    word: "",
    loading: false,
}


function game(state = initialState, action){
    switch (action.type){
        case START_GAME:
            return {...state, 
                gameNumber: state.gameNumber + 1,
                playerName: action.text
            }
        default:
            return state
    }
}

export default game;
