import React, {Component} from 'react';
import { connect } from 'react-redux';
import axios from 'axios'
import SecretWord from '../components/secret';
import SelectCharacters from '../components/characters'
import {startGame, guessCharacter} from '../actions/game';

const url = 'http://app.linkedin-reach.io/words'
const path = '/api/words'
const param = '?difficulty=10'

class Game extends Component {       
    constructor(props){
        super(props)
        this.state = {
            currentWord: "",
            loading: true,
        }
    }

    fetchWord = () => {
        axios.get('/api/words' + param)
        .then(response => {
          return response.data.split("\n")
        })
        .then(allWords => {
          const word = allWords[Math.floor(allWords.length * Math.random())]
          return word
        })
        .then((word)=> {
            console.log(word)
            this.setState({
                currentWord: word,
                loading: false
            })
        })
    }

    componentDidMount(){
        this.fetchWord();
    }

    checkCharacter(char){
        console.log(char);
    }

    render(){
        if (this.state.loading){
            return (
                <div>
                    Loading ...
                </div>
            )
        }
        return (
            <div>
                <h1>Hangman Game</h1>
                <SecretWord
                    currentWord={this.state.currentWord}
                />
                <SelectCharacters
                    onClick={this.checkCharacter}
                />
            </div>
        )
    }
}

const mapStateToProps = store => ({
    gameNumber: store.gameNumber,
})

const mapDispatchToProps = (dispatch) => ({
    startGame: (text) => {dispatch(startGame(text))}
})

export default connect(mapStateToProps,mapDispatchToProps)(Game)
