import React, {Component} from 'react';
import { connect } from 'react-redux';
import axios from 'axios'
import SecretWord from '../components/secret';
// import SelectCharacters from '../components/characters'
import {startGame, guessCharacter} from '../actions/game';

const url = 'http://app.linkedin-reach.io/words'
const path = '/api/words'
const param = '?difficulty=10'

class Game extends Component {       
    constructor(props){
        super(props)
        this.state = {
            currentWord: "",
            loading: true,
            charactersPlayed: "",
            charSlots: [],
            correctlyGuessed: [],
            incorrectlyGuessed: [],
            numberOfGuesses: 6,
            gameOver: false
        }
        this.characters = "abcdefghijklmnopqrstuvwxyz12367890"
    }

    fetchWord = () => {
        axios.get('/api/words' + param)
        .then(response => {
          return response.data.split("\n")
        })
        .then(allWords => {
          const word = allWords[Math.floor(allWords.length * Math.random())]
          return word
        })
        .then((word) => {
            console.log(word)
            this.setState({
                currentWord: word,
                loading: false
            })
        })
    }

    componentDidMount(){
        this.fetchWord();
    }


    checkCharacter = (char) => {
        if (this.state.numberOfGuesses > 0) {
            if (this.state.incorrectlyGuessed.includes(char) || this.state.correctlyGuessed.includes(char)){
                return alert(`You've already used the character: ${char}`)
            } else if (this.state.currentWord.includes(char)) {
                this.state.correctlyGuessed.push(char)
            }
        } else {
            this.setState({gameOver: true})
        }

    }

    createCharacters = () => {
        const keyboard = this.characters.split("")
            .filter((char)=> this.state.incorrectlyGuessed.includes(char) === -1)
            .map((char)=> {
            return (
                <button id={char} key={char} onClick={() => this.checkCharacter(char)}>{char}</button>
            )
        })
        return keyboard
    }

    updateWordSlot = (char) => {
        this.currentWord.split("").forEach((el)=>{
            if (el === char){
                this.setState({correctlyGuessed: this.state.correctlyGuessed.concat(el)})
            }
        })
    }

    updateCharSlots = () => {

    }

    createWordSlot = () => {
        var charSlots = this.state.currentWord.split("").map((el) => "_")
        this.setState({charSlots: charSlots})
    }

    render(){
        if (this.state.loading){
            return (
                <div>
                    Loading ...
                </div>
            )
        } else if (this.state.gameOver){
            return (
                <div>
                    <div>The game is over</div>
                    <div> check winner</div>
                    <div> add button to restart game</div>
                </div>
            )
        } else { 
            return (
                <div>
                    <h1>Hangman Game</h1>
                    <ul>
                        <li>
                            <p>{this.state.currentWord}</p>
                        </li>
                        <li>
                            <p>{this.state.charSlots.join("")}</p>
                        </li>
                        <li>
                            <p>{this.props.guessedCharacters}</p>
                        </li>
                    </ul>
                    {this.createCharacters()}
                </div>
            )
        }
    }
}



const mapStateToProps = store => ({
    gameNumber: store.gameNumber,
})

const mapDispatchToProps = (dispatch) => ({
    startGame: (text) => {dispatch(startGame(text))}
})

export default connect(mapStateToProps,mapDispatchToProps)(Game)
