import React, {Component} from 'react';
import axios from 'axios'
import Keyboard from '../components/keyboard'
import Hangman from '../components/hangman'
import './game.css'

const url = 'http://app.linkedin-reach.io/words'
const path = '/api/words'
const param = '?difficulty=10'

class Game extends Component {       
    constructor(props){
        super(props)
        this.state = {
            currentWord: "",
            loading: true,
            charSlots: [],
            guessed: [],
            correctlyGuessed: [],
            numberOfGuesses: 6,
            gameOver: false
        }
        this.characters = "abcdefghijklmnopqrstuvwxyz12367890";
        this.baseState = this.state;
    }

    fetchWord = () => {
        axios.get('/api/words' + param)
        .then(response => {
          return response.data.split("\n")
        })
        .then(allWords => {
          const word = allWords[Math.floor(allWords.length * Math.random())]
          return word
        })
        .then((word) => {
            const wordHash = word.split("").map((char)=> ({character: char, guessed: false}))
            this.setState({
                currentWord: wordHash,
                loading: false
            })
        })
    }

    componentDidMount(){
        this.fetchWord();
    }

    checkCharacter = (char) => {
        if (this.state.guessed.indexOf(char) === -1){
            this.setState((prevState) => {
                return {
                    guessed: prevState.guessed.concat(char)
                }
            })
        }
        if (this.state.currentWord.includes(char)){
            this.setState(prevState => {
                return {correctlyGuessed: prevState.correctlyGuessed.concat(char)}
            })
        } else {
            this.setState((prevState) => {
                return {numberOfGuesses: prevState.numberOfGuesses - 1}
            })
        }

    }

    checkGameWon = (guessedChars) => {
        return guessedChars.reduce((winState, currentObject) => {
            return winState && currentObject.guessed;
          }, true);
    }

    handleClick = (char, e) => {
        console.log(char, e)
        this.checkCharacter(char);
    }

    handleReset = () => {
        this.setState({...this.baseState});
    }

    loading = () => (<div>Loading..</div>)


    render(){
        if (this.state.loading){
            return this.loading()
        } else if (this.state.gameOver){
            return (
                <div>
                    <div>The game is over</div>
                    <div> check winner</div>
                    <div> add button to restart game</div>
                </div>
            )
        } else { 
            return (
                <div className="game">
                    <h1>Hangman Game</h1>
                    <Hangman
                        gameTurn={this.state.numberOfGuesses}
                    />
                    <Keyboard
                        guesses={this.state.guessed}
                        onClick={this.handleClick}
                    />
                </div>
            )
        }
    }
}

export default Game
